UPDATE M_USER
INSTITUTION_NAME = #{institutionName,jdbcType=VARCHAR}, ADDRESS = #{address,jdbcType=VARCHAR}, CONTACT_NUMBER = #{contactNumber,jdbcType=VARCHAR}, DISTRICT = #{district,jdbcType=VARCHAR}, AREA = #{area,jdbcType=VARCHAR}, CATEGORY_CODE = #{categoryCode,jdbcType=VARCHAR}, UPDATED_BY = #{updatedBy,jdbcType=VARCHAR}, UPDATED_DATETIME = #{updatedDateTime,jdbcType=TIMESTAMP}
WHERE INSTITUTION_ID = #{institutionId,jdbcType=INTEGER}
