UPDATE M_USER
SURNAME = #{surname,jdbcType=VARCHAR}, GIVEN_NAME = #{givenName,jdbcType=VARCHAR}, MIDDLE_INITIAL = #{middleInitial,jdbcType=VARCHAR}, EMAIL_ADDRESS = #{emailAddress,jdbcType=VARCHAR}, MOBILE_NUMBER = #{mobileNumber,jdbcType=VARCHAR}, INSTITUTION_ID = #{institutionId,jdbcType=INTEGER}, UPDATED_BY = #{updatedBy,jdbcType=VARCHAR}, UPDATED_DATETIME = #{updatedDateTime,jdbcType=TIMESTAMP}
WHERE USER_ID = #{userId,jdbcType=INTEGER}
